//초기값
const initailState = {
  list: [],
};

//액션
const CREATE = "post/CREATE";
const LOAD = "post/LOAD";
const DELETE = "post/DELETE";

//액션크리에이터

export function createPost(post) {
  return { type: CREATE, post };
}
export function loadPost(post) {
  return { type: LOAD, post };
}
export function deletePost(post) {
  return { type: DELETE, post };
}

//리듀서
export default function reducer(state = initailState, action = {}) {
  switch (action.type) {
    case "post/CREATE": {
      return { list: [...state.list, action.post] };
    }
    case "post/LOAD": {
      return { list: [action.post] };
    }
    case "post/DELETE": {
      //   let idx = state.list.findIndex((d) => d.id === action.post);
      const filter = state.filter((d) => d.id === action.id);
      return console.log("gww", filter);
    }
    default:
      return state;
  }
}
