import "./App.css";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { createPost, loadPost } from "./redux/modules/post";

function App() {
  // 리액트 상태관리
  const [post, setPost] = React.useState("");
  const dispatch = useDispatch();

  // 리덕스 구독
  const post_data = useSelector((state) => state.post.list);
  console.log("포스트데이터", post_data);

  //로드

  React.useEffect(() => {
    console.log("로드2");
    dispatch(loadPost(w));
  }, []);
  // 포스트 입력값
  const typing = (e) => {
    setPost(e.target.value);
  };
  const write = () => {
    return dispatch(createPost({ post: post }));
  };
  const w = { post: "aaaa" };
  return (
    <>
      <div>안녕하세요 리덕스를 통한 CRUD 구현을 할겁니다</div>
      {/* <div>들어가기전에 리액트로 바꿔보고 </div> */}
      <input onChange={typing}></input>
      <button
        onClick={() => {
          console.log(
            "먼저 콘솔을 찍어서 버튼이 되는지 확인하고요, 확인이 됐으니 글을 써봅시다.",
            "먼저 데이터가 있어야겠죠? 데이터를 준비합시다. "
          );
          write();
        }}
      >
        버튼을 누르면 글을 등록합니다.
      </button>
      {post_data.map((a) => {
        console.log(a);
        return (
          <div>
            <li style={{ marginLeft: "20px" }}>{a.post}</li>
          </div>
        );
      })}
    </>
  );
}

export default App;
