import "./App.css";
import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { createPost, loadPost, deletePost } from "./redux/modules/post";

function App() {
  // 리액트 상태관리
  const [post, setPost] = React.useState("");
  const dispatch = useDispatch();

  // 리덕스 구독
  const post_data = useSelector((state) => state.post.list);
  console.log("포스트데이터", post_data);

  const result = post_data.find((p) => p.id);
  console.log("핳", result);

  //로드
  const w = { post: "aaaa" };
  React.useEffect(() => {
    console.log("로드2");
    dispatch(loadPost(w));
  }, []);
  // 포스트 입력값
  const typing = (e) => {
    setPost(e.target.value);
  };
  const write = () => {
    return dispatch(createPost({ post: post, id: Date.now() }));
  };
  const del = (e) => {
    return dispatch(deletePost(e));
  };
  // const edit = () => {
  //   return dispatch(editPost());
  // }

  return (
    <>
      <div>안녕하세요 리덕스를 통한 CRUD 구현을 할겁니다</div>
      {/* <div>들어가기전에 리액트로 바꿔보고 </div> */}
      <input onChange={typing}></input>
      <button
        onClick={() => {
          write();
        }}
      >
        버튼을 누르면 글을 등록합니다.
      </button>
      {post_data.map((a, i) => {
        return (
          <div key={i}>
            <li style={{ marginLeft: "20px" }}>
              {a.post}
              <button
                onClick={(e) => {
                  e.preventDefault();
                  e.stopPropagation();

                  console.log("수정", a.id);
                }}
              >
                수정하기
              </button>
              <button
                onClick={(e) => {
                  e.preventDefault();
                  e.stopPropagation();
                  console.log("느낌왔어", a.id);
                  del(a.id);
                }}
              >
                X
              </button>
            </li>
          </div>
        );
      })}
    </>
  );
}

export default App;
