//초기값
const initailState = {
  list: [],
};

//액션
const CREATE = "post/CREATE";

//액션크리에이터

export function createPost(post) {
  return { type: CREATE, post: post };
}

//리듀서
export default function reducer(state = null, action = {}) {
  switch (action.type) {
    case "post/CREATE": {
      return console.log("스테이트", { list: [...state.list, action.post] });
    }
    default:
      return state;
  }
}
