//초기값
const initailState = {
  list: [
    { name: "john", age: "25", title: "숨쉬기" },
    { name: "carl", age: "30", title: "밥먹기" },
    { name: "mac", age: "35", title: "달리기" },
  ],
};

//액션
const CREATE = "post/CREATE";

//액션크리에이터

export function createPost(post) {
  return { type: CREATE, post: post };
}

//리듀서
export default function reducer(state = initailState, action = {}) {
  switch (action.type) {
    case "post/CREATE": {
      return console.log("크리에이트 액션 디스패치");
    }
    default:
      return state;
  }
}
